<template>
    <div class="rating">
        <template v-if="avg!==0">
            <template v-for="(val,key) in stars">
                <span :class="[key,'star']" v-for="n in val"></span>
            </template>
            <span style="margin-left:1rem">{{avg}}</span>
        </template>
        <template v-else>
            <span>暂无评分</span>
        </template>
    </div>
</template>

<script>
export default {
    name: 'rating',
    props: ['avg'],
    computed: {
        stars() {
            let full = parseInt(this.avg / 2) + (this.avg % 2 >= 1 ? 1 : 0),
                gray = 5 - full;
            return { full, gray }
        }
    }
}
</script>

<style lang="scss">
.rating {
    display: flex;
    height: 2rem;
    align-items: center;
    justify-content: center;
    span{
        color: #9b9b9b;
        font-size: 1.3rem;
    }
    .star {
        width: 1rem;
        height: 1rem;
        display: inline-block;
        background-size: cover;
    }
    .full {
        background-image: url("data:image/png;base64,");
    }
    .gray {
        background-image: url("data:image/png;base64,");
    }
}
</style>
